function formatDateYYYYMMDD(date) {
  const year = date.getFullYear();
  const month = String(date.getMonth() + 1).padStart(2, '0'); // Months are 0-based
  const day = String(date.getDate()).padStart(2, '0');
  return `${year}-${month}-${day}`;
}

const serverNow = new Date();

const userTZ =  $input.first().json.timezone; // e.g., "Asia/Kolkata"

const userTime = new Date(serverNow.toLocaleString("en-US", { timeZone: userTZ }));

const hour = userTime.getHours();

const formattedToday = formatDateYYYYMMDD(userTime);


// Previous date in user's timezone
userTime.setDate(userTime.getDate() - 1);
const formattedYesterday = formatDateYYYYMMDD(userTime);

return [{ json: { hour: hour, currentDate:formattedToday, prevDate: formattedYesterday } }];

